package y2016.day02;
import java.util.Scanner;

import y2016.day02.KeyPad.Direction;

public class bathroomKeypad {
	
	static String input = "\n"+
			"\n"+
			"\n"+
			"\n"+
			"";
	
//	static String input = "ULL\n"
//			+ "RRDDD\n"
//			+ "LURDL\n"
//			+ "UUUUD";
	static KeyPad kp = new KeyPad();
	
	public static void main(String[] args) {
		Scanner sc = new Scanner(input);
		sc.useDelimiter("\n");
		while(sc.hasNext()){
			String keyString = sc.next();
			
			for (int i = 0; i < keyString.length(); i++) {
				move(keyString.charAt(i));
			}
			System.out.println(kp.getCurrentLocation());
		}
		
		sc.close();
	}
	
	
	private static void move(char c) {
		switch (c){
		case 'U':
			kp.move(Direction.UP);
			break;
		case 'D':
			kp.move(Direction.DOWN);
			break;
		case 'L':
			kp.move(Direction.LEFT);
			break;
		case 'R':
			kp.move(Direction.RIGHT);
			break;			
		}
		
	}
}
